pub fn obfuscate(input: &str) -> String {
    let mut output = String::new();
    
    if input.len() == 0 {
        return output;
    }
    
    let mut tokenizer = Tokenizer::new(input);
    tokenizer.parse(&mut output);
    
    output
}

pub fn wrap_eval(input: &mut String) {
    input.insert_str(0, JSFUCK_EVAL_WRAP);
    input.push(')');
}